import React, { Component } from "react";
import { Grid, Image, Segment } from 'semantic-ui-react'


class SearchForm extends Component {
constructor(props){
super(props);
this.onSubmit = this.onSubmit.bind(this);
this.state = {
InputText: '',
images: [],
};
this.handleSearchTermChange = this.handleSearchTermChange.bind(this);
}
FetchImages(){
var searchTerm=this.state.InputText;
fetch('https://api.flickr.com/services/rest/?method=flickr.photos.search&api_key=22b7d5bde5eb0e61538db8f0206e48a6&tags='+searchTerm+'&per_page=100&page=1&format=json&nojsoncallback=1')
.then(function(response){
return response.json();
})
.then(function(j){
alert(JSON.stringify(j));
let picArray = j.photos.photo.map((pic) => {
var imageSrc = 'https://farm'+pic.farm+'.staticflickr.com/'+pic.server+'/'+pic.id+'_'+pic.secret+'.jpg';
return(
<img alt="searchTerm" src={imageSrc}></img>
)
})
this.setState({images: picArray});
}.bind(this))
}
handleSearchTermChange(event){
console.log("search term changed");
this.setState( {InputText:event.target.value} );
} 
onSubmit(event) {
event.preventDefault();
console.log(this.state.InputText);
this.FetchImages();
}
createTable() {
let table = []
for (let i = 0; i < this.state.images.length; i++) {
table.push(
<Grid.Column mobile={16} tablet={8} computer={4}>
   {this.state.images[i]}
</Grid.Column>
)
}
return table
}
render() {
return (
<div id="search-form">
   <form onSubmit={this.onSubmit}>
      <p className="header">Enter Text</p>
      <input type="text" name="InputText" onChange={this.handleSearchTermChange}  required="required" />
      <input type="submit" className="submit-button" defaultValue="Search" />
   </form>
   <div id='sel'>
      <Grid doubling columns={5} >
         <Grid.Row>
            {this.createTable()}
         </Grid.Row>
      </Grid>
   </div>
</div>
);
}
}
export default (SearchForm);
